import './App.css'; // untuk CSS styling
import React, { useEffect, useState } from 'react';
import MovieCard from './utils/components/MovieCard.jsx';
import WatchedList from './utils/components/WatchedList.jsx';
import { getWatchedMovies, saveWatchedMovies } from './utils/storage';
import useOnlineStatus from './utils/hooks/useOnlineStatus';
import { v4 as uuidv4 } from 'uuid';

// Contoh data film
const moviesData = [
  
  { id: '1', title: 'Inception', year: 2010, thumbnail: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRuXxd1Ac-DeglLiAqVsvW2dkmLYb9FteNH-g&s', description: 'A thief who steals corporate secrets through the use of dream-sharing technology is given the inverse task of planting an idea into the mind of a CEO.', ratting: '8.8', ganre: 'Sci-Fi', director: 'Christopher Nolan' },
  
  { id: '2', title: 'Interstellar', year: 2014, thumbnail: 'data:image/jpeg;base64,', description: 'A dystopian future where humanity is unknowingly trapped inside a simulated reality, the Matrix, created by intelligent machines to distract humans while their bodies are used as an energy source.' , ratting : '8.8', ganre: 'Action, Sci-Fi', director: 'Lana Wachowski, Lilly Wachowski' },
  { id: '3', title: 'The Matrix', year: 1999, thumbnail: 'data:image/jpeg;base64,', description: 'A thrilling action movie with stunning visuals and a gripping storyline.', ratting: '8.8', ganre: 'Action', director: 'John Doe' },
];

function App() {
  const [watchedMovies, setWatchedMovies] = useState(getWatchedMovies());
  const isOnline = useOnlineStatus();

  const handleWatch = (movie) => {
    const updated = [...watchedMovies, movie];
    setWatchedMovies(updated);
    saveWatchedMovies(updated);
    if (isOnline) {
      // simulasikan sinkronisasi
      console.log("Synced to server (dummy)");
    } else {
      console.log("Saved locally. Will sync when online.");
    }
  };

  // Saat online kembali
  useEffect(() => {
    if (isOnline) {
      console.log("Syncing watched list to server...");
      // Simulasi sinkronisasi, kalau nyata, pakai fetch/axios ke backend
    }
  }, [isOnline]);

  const isWatched = (movieId) => watchedMovies.some((m) => m.id === movieId);

  return (
    <div className="app-container">
      <header className="app-header">
      <h1>Techtest Movie Web</h1> 
      <p>Selamat datang di aplikasi film kami!</p>
      </header>

      <input placeholder="CARI FILM.." className="movie-search" />
      <p>Status: {isOnline ? "ðŸŸ¢ Online" : "ðŸ”´ Offline"}</p>
      
      <h2>All Movies</h2>
      {moviesData.map((movie) => (
        <MovieCard
          key={movie.id}
          movie={movie}
          onWatch={handleWatch}
          watched={isWatched(movie.id)}
        />
      ))}

      <WatchedList movies={watchedMovies} />
      
    </div>
  );
}

export default App;
